INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (1, 'Angular', 'A platform for building mobile and desktop web applications.', 'https://angular.dev/', 'angular.svg', '#DD0031', '#C3002F');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (2, 'Node.js', 'A JavaScript runtime built on Chrome\'s V8 JavaScript engine.', 'https://nodejs.org/', 'nodejs.svg', '#339933', '#333333');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (3, 'React', 'A JavaScript library for building user interfaces.', 'https://reactjs.org/', 'react.svg', '#61DAFB', '#20232A');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (4, 'Vue.js', 'The Progressive JavaScript Framework.', 'https://vuejs.org/', 'vuejs.svg', '#4FC08D', '#35495E');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (5, 'HTML', 'The standard markup language for creating web pages. It defines the structure and content of web pages using elements that tell the browser how to display content.', 'https://html.spec.whatwg.org/', 'html.svg', '#E44D26', '#F16529');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (6, 'CSS', 'Cascading Style Sheets (CSS) is a stylesheet language used to describe the presentation of a document written in HTML or XML. CSS defines how elements should be displayed on screen, paper, or in other media.', 'https://www.w3.org/Style/CSS/Overview.en.html', 'css.svg', '#1572B6', '#1D365D');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (7, 'JavaScript', 'JavaScript (JS) is a lightweight, interpreted programming language with first-class functions. It is most well-known as the scripting language for web pages, but many non-browser environments also use it, such as Node.js, Apache CouchDB, and Adobe Acrobat.', 'https://developer.mozilla.org/en-US/docs/Web/JavaScript', 'javascript.svg', '#F7DF1E', '#323330');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (8, 'TypeScript', 'TypeScript is a strongly typed programming language that builds on JavaScript, giving you better tooling at any scale. It is a syntactic superset of JavaScript that adds optional static typing and other features.', 'https://www.typescriptlang.org/', 'typescript.svg', '#007ACC', '#1E2A3E');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (9, 'Svelte', 'Svelte is a UI framework that uses a compiler to let you write breathtakingly concise components that do minimal work in the browser, using languages you already know — HTML, CSS and JavaScript.', 'https://svelte.dev/', 'svelte.svg', '#FF3E00', '#FF4D00');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (10, 'Solid.js', 'Solid is a declarative, efficient and flexible JavaScript library for building user interfaces.', 'https://solidjs.com/', 'solidjs.svg', '#204A2C', '#2D6A4F');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (11, 'Qwik', 'Qwik is a new kind of web framework that can deliver instant loading web applications at any size or complexity. Your sites and apps can boot with about 1kb of JS, and achieve consistent performance at scale.', 'https://qwik.dev/', 'qwik.svg', '#FF5A00', '#FF6A00');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (12, 'Preact', 'Preact provides the thinnest possible Virtual DOM abstraction on top of the DOM. It builds on stable platform features, registers real event handlers and plays nicely with other libraries.', 'https://preactjs.com/', 'preact.svg', '#FF5C00', '#FF6F00');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (13, 'Alpine.js', 'Alpine is a rugged, minimal tool for composing behavior directly in your markup. Think of it like jQuery for the modern web.', 'https://alpinejs.dev/', 'alpinejs.svg', '#4C8BF5', '#3B7DFF');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (14, 'Lit', 'Lit is a simple, fast web component library built on Web Components standards, with a small footprint and fast rendering. It is interoperable and future-ready.', 'https://lit.dev/', 'lit.svg', '#FF3E00', '#FF4D00');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (15, 'Tailwind CSS', 'Tailwind CSS is a utility-first CSS framework for rapidly building modern websites without ever leaving your HTML.', 'https://tailwindcss.com/', 'tailwindcss.svg', '#38BDF8', '#0EA5E9');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (16, 'Bootstrap', 'Bootstrap is the most popular HTML, CSS, and JS framework for developing responsive, mobile first projects on the web.', 'https://getbootstrap.com/', 'bootstrap.svg', '#7952B3', '#6F42C1');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (17, 'Bulma', 'Bulma is a free, open-source CSS framework based on Flexbox and built with Sass.', 'https://bulma.io/', 'bulma.svg', '#00D1B2', '#00C4B4');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (18, 'Foundation', 'Foundation is a responsive front-end framework similar to Bootstrap, but with more flexible grid options and advanced features.', 'https://get.foundation/', 'foundation.svg', '#F1C40F', '#F39C12');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (19, 'Material UI', 'Material UI is a popular React UI framework that implements Google\'s Material Design system.', 'https://mui.com/', 'materialui.svg', '#1976D2', '#1565C0');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (20, 'Chakra UI', 'Chakra UI is a simple, modular and accessible component library that gives you the building blocks you need to build your React applications.', 'https://chakra-ui.com/', 'chakraui.svg', '#319795', '#2C7A7B');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (21, 'Sass (SCSS)', 'Sass is a mature, stable, and powerful CSS extension language that adds variables, nested rules, and more to traditional CSS, helping to write maintainable and scalable styles.', 'https://sass-lang.com/', 'sass.svg', '#CC6699', '#CF649A');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (22, 'Less', 'Less is a backward-compatible language extension for CSS that adds features like variables, mixins, and functions to make stylesheets more maintainable.', 'https://lesscss.org/', 'less.svg', '#1D365D', '#2B4C80');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (23, 'PostCSS', 'PostCSS is a tool for transforming CSS with JavaScript plugins. These plugins can lint your CSS, support variables and mixins, transpile future CSS syntax, and much more.', 'https://postcss.org/', 'postcss.svg', '#DD3A0A', '#CC342D');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (24, 'Canvas', 'The HTML canvas element provides a surface for rendering graphics, animations, or other visual images on the fly using JavaScript.', 'https://developer.mozilla.org/en-US/docs/Web/API/Canvas_API', 'canvas.svg', '#E34F26', '#EF652A');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (25, 'WebGL', 'WebGL is a JavaScript API for rendering high-performance interactive 2D and 3D graphics within any compatible web browser without the use of plug-ins.', 'https://www.khronos.org/webgl/', 'webgl.svg', '#990000', '#CC0000');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (26, 'Three.js', 'Three.js is a cross-browser JavaScript library and API used to create and display animated 3D computer graphics in a web browser using WebGL.', 'https://threejs.org/', 'threejs.svg', '#000000', '#222222');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (27, 'D3.js', 'D3.js is a JavaScript library for producing dynamic, interactive data visualizations in web browsers using HTML, SVG, and CSS.', 'https://d3js.org/', 'd3js.svg', '#F9A03C', '#FF6600');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (28, 'Chart.js', 'Chart.js is a simple yet flexible JavaScript charting library for designers and developers, based on the HTML5 canvas element.', 'https://www.chartjs.org/', 'chartjs.svg', '#FF6384', '#36A2EB');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (29, 'Recharts', 'Recharts is a composable charting library built on React components and D3, designed for simplicity and performance.', 'https://recharts.org/', 'recharts.svg', '#22B5BF', '#0088FE');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (30, 'Next.js', 'Next.js is a React framework that enables functionality such as server-side rendering and static site generation for building fast, scalable web applications.', 'https://nextjs.org/', 'nextjs.svg', '#000000', '#111111');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (31, 'Nuxt.js', 'Nuxt.js is an open-source framework based on Vue.js that helps developers build server-side rendered, statically generated, or single-page applications easily.', 'https://nuxt.com/', 'nuxtjs.svg', '#00DC82', '#2F495E');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (32, 'Astro', 'Astro is a modern static site builder that delivers lightning-fast websites using a unique architecture that ships zero JavaScript by default.', 'https://astro.build/', 'astro.svg', '#BC52EE', '#FF5D01');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (33, 'Remix', 'Remix is a full-stack web framework focused on fast performance, leveraging web standards to provide great developer and user experiences.', 'https://remix.run/', 'remix.svg', '#000000', '#1F1F1F');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (34, 'Electron', 'Electron is a framework for building cross-platform desktop applications with JavaScript, HTML, and CSS using Node.js and Chromium.', 'https://www.electronjs.org/', 'electron.svg', '#47848F', '#2B2E3A');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (35, 'Expo SDK', 'Expo is an open-source platform for making universal native apps for Android, iOS, and the web with JavaScript and React.', 'https://expo.dev/', 'exposdk.svg', '#000020', '#4630EB');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (36, 'React Native Web', 'React Native Web makes it possible to run React Native components and APIs on the web using React DOM.', 'https://necolas.github.io/react-native-web/', 'reactnativeweb.svg', '#61DAFB', '#20232A');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (37, 'Python', 'Python is a high-level, interpreted programming language known for its readability and versatility in web development, data science, automation, and more.', 'https://www.python.org/', 'python.svg', '#3776AB', '#FFD43B');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (38, 'Django', 'Django is a high-level Python web framework that encourages rapid development and clean, pragmatic design.', 'https://www.djangoproject.com/', 'django.svg', '#092E20', '#44B78B');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (39, 'Flask', 'Flask is a lightweight and flexible Python web framework that provides tools, libraries, and technologies for building web applications.', 'https://flask.palletsprojects.com/', 'flask.svg', '#000000', '#FFFFFF');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (40, 'Fast API', 'FastAPI is a modern, fast (high-performance) web framework for building APIs with Python based on standard type hints.', 'https://fastapi.tiangolo.com/', 'fastapi.svg', '#009688', '#00BFA5');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (41, 'PHP', 'PHP is a popular general-purpose scripting language that is especially suited to web development. It is fast, flexible, and pragmatic.', 'https://www.php.net/', 'php.svg', '#777BB4', '#8892BF');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (42, 'Laravel', 'Laravel is a web application framework with expressive, elegant syntax that makes developing PHP applications faster and easier.', 'https://laravel.com/', 'laravel.svg', '#FF2D20', '#EF3B2D');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (43, 'Symfony', 'Symfony is a PHP framework and a set of reusable PHP components for building web applications, APIs, and microservices.', 'https://symfony.com/', 'symfony.svg', '#000000', '#FFFFFF');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (44, 'Ruby', 'Ruby is a dynamic, open-source programming language with a focus on simplicity and productivity, featuring an elegant syntax that is easy to read and write.', 'https://www.ruby-lang.org/', 'ruby.svg', '#CC342D', '#701516');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (45, 'Ruby on Rails', 'Ruby on Rails is a server-side web application framework written in Ruby that follows the Model-View-Controller (MVC) pattern.', 'https://rubyonrails.org/', 'rubyonrails.svg', '#CC0000', '#A30000');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (46, 'Java', 'Java is a high-level, class-based, object-oriented programming language designed to have as few implementation dependencies as possible.', 'https://www.java.com/', 'java.svg', '#007396', '#5382A1');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (47, 'Spring Boot', 'Spring Boot is an open-source Java-based framework used to create stand-alone, production-grade Spring applications with minimal configuration.', 'https://spring.io/projects/spring-boot', 'springboot.svg', '#6DB33F', '#5BA830');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (48, 'Quarkus', 'Quarkus is a Kubernetes-native Java framework tailored for GraalVM and OpenJDK HotSpot, crafted to optimize Java specifically for containers.', 'https://quarkus.io/', 'quarkus.svg', '#4695EB', '#3A7BDC');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (49, 'Micronaut', 'Micronaut is a modern, JVM-based, full-stack framework for building modular, easily testable microservice and serverless applications.', 'https://micronaut.io/', 'micronaut.svg', '#00A9A5', '#007D79');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (50, 'C#', 'C# is a modern, object-oriented programming language developed by Microsoft that runs on the .NET platform and is used for web, mobile, and desktop applications.', 'https://learn.microsoft.com/en-us/dotnet/csharp/', 'csharp.svg', '#68217A', '#512BD4');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (51, '.NET', '.NET is a free, cross-platform, open-source developer platform for building many different types of applications using C#, F#, or Visual Basic.', 'https://dotnet.microsoft.com/', 'dotnet.svg', '#512BD4', '#68217A');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (52, 'ASP.NET Core', 'ASP.NET Core is a cross-platform, high-performance, open-source framework for building modern, cloud-based, and internet-connected applications.', 'https://learn.microsoft.com/en-us/aspnet/core/', 'aspdotnetcore.svg', '#512BD4', '#2400A5');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (53, 'Go', 'Go (or Golang) is an open-source programming language designed for simplicity, reliability, and efficiency, developed by Google.', 'https://go.dev/', 'go.svg', '#00ADD8', '#00A3C4');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (54, 'Gin', 'Gin is a high-performance HTTP web framework written in Go (Golang) that features a Martini-like API with much better performance.', 'https://gin-gonic.com/', 'gin.svg', '#00ADD8', '#0099CC');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (55, 'Fiber', 'Fiber is an Express-inspired web framework built on top of Fasthttp, the fastest HTTP engine for Go, designed to simplify rapid web development.', 'https://gofiber.io/', 'fiber.svg', '#00C7B7', '#009F94');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (56, 'Echo', 'Echo is a high-performance, extensible, minimalist Go web framework that provides robust routing, middleware support, and optimized performance.', 'https://echo.labstack.com/', 'echo.svg', '#132E48', '#1B4060');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (57, 'Rust', 'Rust is a systems programming language focused on performance, memory safety, and concurrency, empowering developers to build reliable and efficient software.', 'https://www.rust-lang.org/', 'rust.svg', '#DEA584', '#000000');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (58, 'Actix', 'Actix is a powerful, pragmatic, and extremely fast web framework for Rust, built on top of the Actix actor framework.', 'https://actix.rs/', 'actix.svg', '#000000', '#333333');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (59, 'Rocket', 'Rocket is a web framework for Rust that makes it simple to write fast, secure web applications without sacrificing flexibility or type safety.', 'https://rocket.rs/', 'rocket.svg', '#C00000', '#FF3D00');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (60, 'Axum', 'Axum is an ergonomic and modular web framework built with Tokio, Tower, and Hyper for building asynchronous web applications in Rust.', 'https://github.com/tokio-rs/axum', 'axum.svg', '#425A70', '#0A66C2');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (61, 'Kotlin', 'Kotlin is a modern, concise, and safe programming language that runs on the JVM and is fully interoperable with Java.', 'https://kotlinlang.org/', 'kotlin.svg', '#7F52FF', '#A97BFF');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (62, 'Ktor', 'Ktor is an asynchronous framework built by JetBrains for creating microservices and web applications in Kotlin.', 'https://ktor.io/', 'ktor.svg', '#0F3A64', '#2E7DD7');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (63, 'Elixir', 'Elixir is a dynamic, functional language designed for building scalable and maintainable applications, leveraging the Erlang VM.', 'https://elixir-lang.org/', 'elixir.svg', '#4B275F', '#6E3D86');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (64, 'Phoenix', 'Phoenix is a web development framework written in Elixir that implements the server-side MVC pattern and focuses on speed and maintainability.', 'https://www.phoenixframework.org/', 'phoenix.svg', '#F05423', '#E44D26');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (65, 'Scala', 'Scala is a hybrid functional and object-oriented programming language that runs on the JVM and is designed to be concise, elegant, and type-safe.', 'https://www.scala-lang.org/', 'scala.svg', '#DC322F', '#CC0000');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (66, 'Play Framework', 'Play Framework is a reactive web application framework for Java and Scala that enables building scalable and fast web applications.', 'https://www.playframework.com/', 'playframework.svg', '#64A70B', '#74B816');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (67, 'Express.js', 'Express.js is a fast, unopinionated, minimalist web framework for Node.js, designed for building web applications and APIs.', 'https://expressjs.com/', 'expressjs.svg', '#000000', '#444444');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (68, 'Nest.js', 'NestJS is a progressive Node.js framework for building efficient, reliable, and scalable server-side applications using TypeScript.', 'https://nestjs.com/', 'nestjs.svg', '#E0234E', '#CC1F4A');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (69, 'AdonisJS', 'AdonisJS is a fully-featured web framework for Node.js, offering an elegant and consistent API inspired by Laravel.', 'https://adonisjs.com/', 'adonisjs.svg', '#5A45FF', '#2F2F8F');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (70, 'Hapi.js', 'Hapi.js is a rich framework for building applications and services in Node.js, known for its powerful plugin system and configuration-driven approach.', 'https://hapi.dev/', 'hapijs.svg', '#373277', '#5C4EE5');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (71, 'Koa.js', 'Koa.js is a lightweight and expressive middleware framework for Node.js designed by the creators of Express to provide a smaller, more robust foundation for web applications.', 'https://koajs.com/', 'koajs.svg', '#333333', '#88D4AB');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (72, 'Fastify', 'Fastify is a fast and low-overhead web framework for Node.js focused on developer experience and high performance.', 'https://fastify.dev/', 'fastify.svg', '#000000', '#00E5FF');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (73, 'Deno', 'Deno is a modern runtime for JavaScript and TypeScript built in Rust, focused on security, simplicity, and native TypeScript support.', 'https://deno.com/', 'deno.svg', '#000000', '#999999');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (74, 'Bun', 'Bun is a fast all-in-one JavaScript runtime, bundler, and package manager built with Zig, designed to replace Node.js and npm.', 'https://bun.sh/', 'bun.svg', '#000000', '#FBF4E4');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (75, 'CodeIgniter', 'CodeIgniter is a powerful PHP framework with a small footprint, built for developers who need a simple and elegant toolkit to create full-featured web applications.', 'https://codeigniter.com/', 'codeigniter.svg', '#EE4323', '#F36C3D');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (76, 'PostgreSQL', 'PostgreSQL is a powerful, open-source object-relational database system known for its reliability, robustness, and advanced features.', 'https://www.postgresql.org/', 'postgresql.svg', '#336791', '#699EC9');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (77, 'MySQL', 'MySQL is an open-source relational database management system known for its speed, reliability, and ease of use.', 'https://www.mysql.com/', 'mysql.svg', '#4479A1', '#F29111');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (78, 'MariaDB', 'MariaDB is an open-source relational database compatible with MySQL, developed by the original creators of MySQL for better performance and security.', 'https://mariadb.org/', 'mariadb.svg', '#003545', '#1F305F');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (79, 'SQLite', 'SQLite is a lightweight, self-contained SQL database engine that is embedded directly into applications.', 'https://www.sqlite.org/', 'sqlite.svg', '#003B57', '#86BBE5');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (80, 'Microsoft SQL Server', 'Microsoft SQL Server is a relational database management system developed by Microsoft, designed for enterprise-grade data storage, processing, and analytics.', 'https://www.microsoft.com/sql-server', 'microsoftsqlserver.svg', '#CC2927', '#555555');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (81, 'Oracle Database', 'Oracle Database is a multi-model relational database management system known for its scalability, performance, and security for enterprise applications.', 'https://www.oracle.com/database/', 'oracledatabase.svg', '#F80000', '#000000');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (82, 'MongoDB', 'MongoDB is a popular NoSQL database that uses a flexible, document-oriented data model for scalability and developer agility.', 'https://www.mongodb.com/', 'mongodb.svg', '#47A248', '#001E2B');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (83, 'Redis', 'Redis is an open-source, in-memory data store used as a database, cache, and message broker for high-performance applications.', 'https://redis.io/', 'redis.svg', '#DC382D', '#A41E11');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (84, 'Apache Cassandra', 'Apache Cassandra is a distributed NoSQL database designed to handle large amounts of data with high availability and fault tolerance.', 'https://cassandra.apache.org/', 'apachecassandra.svg', '#1287B1', '#204F65');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (85, 'CouchDB', 'Apache CouchDB is an open-source NoSQL database that uses a document-oriented model and offers easy replication and RESTful access.', 'https://couchdb.apache.org/', 'couchdb.svg', '#E42528', '#444444');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (86, 'DynamoDB', 'Amazon DynamoDB is a fully managed NoSQL database service that provides fast and predictable performance with seamless scalability.', 'https://aws.amazon.com/dynamodb/', 'dynamodb.svg', '#4053D6', '#232F3E');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (87, 'Firestore', 'Cloud Firestore is a flexible, scalable NoSQL cloud database from Firebase and Google Cloud for real-time and offline data synchronization.', 'https://firebase.google.com/docs/firestore', 'firestore.svg', '#FFCA28', '#039BE5');
INSERT INTO technology (id, name, description, url, logo_path, color_primary, color_secondary) VALUES (88, 'Firebase Realtime Database', 'Firebase Realtime Database is a cloud-hosted NoSQL database that stores and syncs data in real time across all clients.', 'https://firebase.google.com/docs/database', 'firebaserealtimedatabase.svg', '#039BE5', '#FFCA28');
